import {useState} from "react";

const NyTimesDashboard = () => {

    const [name, setName] = useState('Name');  // State to track the selected month (default is January, represented by 1)
    const [articles, setArticles] = useState([]); // State to store the list of articles fetched from the API

    // Handles changes in the month input field and updates the `month` state
    const handleInput = (e) => {
        setName(e.target.value);
    }

    // Fetches articles from the NY Times API for the selected month
    const getArticles = (e) => {
        if (e) {
            e.preventDefault();
        }

        // Making a GET request to the API with the selected month as a query parameter
        fetch(`${process.env.REACT_APP_API_ARTICLES}?name=${name}`)
            .then((response) => {
                // Check if the response is successful, otherwise throw an error
                if (!response.ok) throw new Error("Error fetching data");
                return response.json();
            })
            .then((data) => {
                // Update the articles state with the fetched article data
                setArticles(data.response.docs);
            })
            .catch((error) => {
                // Log any errors that occur during the fetch process
                console.error(error);
            });
    };

    // Create the form
    return (
        <div>
            {/* Title */}
            <h1>New York Times Article Search</h1>

            {/* Create the form that includes the button and the user input to search articles */}
            <form>
                <input type={"text"}
                       name={"month"}
                       id={"month"}
                       onChange={handleInput}
                />
                <button onClick={getArticles}>
                    Search
                </button>
            </form>

            {articles && (
                <div>
                    {articles.map((articles, index) => (
                        <div key={index}>
                            <h2>{articles.headline.main}</h2>
                            <p>{articles.abstract}</p>
                            <p>{articles.source}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>

    );
};

export default NyTimesDashboard;
